!(function (o) {
    o(function () {
        var l = 4;
        if (o(window).width() < 600) {
            l = 4;
            l--;
        } else if (999 < o(window).width()) {
            l = 4;
            l++;
        } else l = 4;
        o(document).ready(function () {
            o(".one").owlCarousel({ nav: !0, items: 1 }),
                o(".two").owlCarousel({
                    nav: !0,
                    margin: 15,
                    mouseDrag: !1,
                    touchDrag: !1,
                    responsive: {
                        0: { items: 3, slideBy: 3 },
                        600: { items: 4, slideBy: 4 },
                        1e3: { items: 5, slideBy: 5 },
                    },
                });
            var e = o(".one");
            e.owlCarousel(),
                e.on("translated.owl.carousel", function (e) {
                    o(".slider-two .item").removeClass("active");
                    var i = o(".slider .owl-item.active").index();
                    o(".slider-two .item").eq(i).addClass("active");
                    var t = Math.ceil((i + 1) / l) - 1;
                    o(".slider-two .owl-dots .owl-dot").eq(t).trigger("click");
                }),
                o(".right").click(function () {
                    o(".slider .owl-next").trigger("click");
                }),
                o(".left").click(function () {
                    o(".slider .owl-prev").trigger("click");
                }),
                o(".slider-two .item").click(function () {
                    var e = o(".item").index(this);
                    o(".slider .owl-dots .owl-dot").eq(e).trigger("click"),
                        o(".slider-two .item").removeClass("active"),
                        o(this).addClass("active");
                }),
                o(".two").owlCarousel(),
                o(".right-t").click(function () {
                    o(".slider-two .owl-next").trigger("click");
                }),
                o(".left-t").click(function () {
                    o(".slider-two .owl-prev").trigger("click");
                });
        });
    });
})(jQuery);
